let contactForm=document.getElementById("contact-form");async function handleContactSubmit(t){t.preventDefault();let e=document.getElementById("contact-form-status"),n=new FormData(t.target);fetch(t.target.action,{method:contactForm.method,body:n,headers:{Accept:"application/json"}}).then((t=>{e.innerHTML="Muchas gracias por escribirnos!",contactForm.reset()})).catch((t=>{e.innerHTML="Oops! Encontramos un problema al enviar tu consulta."}))}contactForm.addEventListener("submit",handleContactSubmit);let orderForm=document.getElementById("order-form");async function handleOrderSubmit(t){t.preventDefault();let e=document.getElementById("order-form-status"),n=new FormData(t.target);fetch(t.target.action,{method:orderForm.method,body:n,headers:{Accept:"application/json"}}).then((t=>{e.innerHTML="Pedido confirmado!",orderForm.reset()})).catch((t=>{e.innerHTML="Oops! Encontramos un problema al enviar tu pedido."}))}orderForm.addEventListener("submit",handleOrderSubmit);